import React, { Component } from 'react';
import { AppBar, RaisedButton, TextField } from 'material-ui';
import {
  Table,
  TableBody,
  TableHeader,
  TableHeaderColumn,
  TableRow,
  TableRowColumn,
} from 'material-ui/Table';
import $ from 'jquery';
var api_url = '/api/login/';
var api_url_scores = '/api/scores';

class Login extends Component {
    constructor(props) {
        super(props);

        var scores = this.getScores();

        this.state = {
            username: '',
            password: '',
            username_error_text: '',
            password_error_text: '',
            zero: scores[0] ? scores[0] : 0,
            one: scores[1] ? scores[1] : 0,
            two: scores[2] ? scores[2] : 0
        }

        this.clear = this.clear.bind(this);
    }

    render() {
        return (
            <div align="center">
                <div>
                  <AppBar title = "Login" />
                  <TextField 
                  hintText = "Please enter your username" 
                  errorText={this.state.username_error_text}
                  value = {this.state.username}
                  onChange = {(event, newValue) => this.setState({username: newValue})}
                  />
                  <br />
                  <TextField
                  type = "password"
                  hintText = "password"
                  floatingLabelText = "Please enter your password"
                  errorText={this.state.password_error_text}
                  value = {this.state.password}
                  onChange = {(event,newValue) => this.setState({password: newValue})}
                  />
                  <br />
                  <RaisedButton 
                  label = "Login" 
                  secondary = {true} 
                  style = {style} 
                  onClick = {(event) => this.validateUserInput(event)}
                  />
                  <RaisedButton 
                  label = "Clear" 
                  secondary = {true} 
                  style = {style} 
                  onClick = {this.clear}
                  />
                  <Table style={{ width: 200}}>
                    <TableHeader displaySelectAll={false}>
                      <TableRow>
                        <TableHeaderColumn>#</TableHeaderColumn>
                        <TableHeaderColumn>Score</TableHeaderColumn>
                      </TableRow>
                    </TableHeader>
                    <TableBody displayRowCheckbox={false} >
                      <TableRow>
                        <TableRowColumn>1</TableRowColumn>
                        <TableRowColumn>{this.state.zero}</TableRowColumn>
                      </TableRow>
                      <TableRow>
                        <TableRowColumn>2</TableRowColumn>
                        <TableRowColumn>{this.state.one}</TableRowColumn>
                      </TableRow>
                      <TableRow>
                        <TableRowColumn>3</TableRowColumn>
                        <TableRowColumn>{this.state.two}</TableRowColumn>
                      </TableRow>
                    </TableBody>
                  </Table>
                </div>
            </div>
        )
    }

    clear = () => {
      this.setState({
        username: '',
        password: ''
      });
    }

    validateUsername() {
      if (!this.state.username) {
        this.setState({username_error_text: 'This field is required'});
        return false;
      } else {
        this.setState({username_error_text: ''});
        return true;
      }
    };

    validatePassword() {
      if (!this.state.password) {
        this.setState({password_error_text: 'This field is required'});
        return false;
      } else if (this.state.password.length < 6) {
        this.setState({password_error_text: 'Password is too short. Minimum 6 characters'})
        return false;
      } else {
        this.setState({password_error_text: ''});
        return true;
      }
    };

    validateUserInput(event) {
      event.preventDefault();
      if (!this.validateUsername() || !this.validatePassword()) {
        alert("Invalid Login");
      } else {
        this.submit();
      }
    };

    getScores() {
      var returnedData;
      $.ajax({
        type: 'GET',
        url: api_url_scores,
        success: function (data) {
          console.log(data);
          returnedData = data;
        },
        error: function (xhr, err) {
          returnedData = "error";
        },
        async: false
      });
      return returnedData;
    };

    submit() {
        console.log("clicked login");
        var payload = {
            "username": this.state.username,
            "password": this.state.password
        }

        var xhr = $.ajax({
            type: 'POST',
            url: api_url,
            data: payload,
            success: function (data) {
                alert("Logged in!");
            },
            error: function (xhr, err) {
                alert("invalid username or password. please try again.");
            }
        });

        xhr.done(data => {
            console.log("Login successful!");
            this.props.loginFunc(data["token"], data["username"]);
        });

        xhr.fail(err => console.log(err));
    }
    };

const style = {
  margin: 15
};

export default Login;
