import React, { Component } from 'react';
import { AppBar, RaisedButton, TextField } from 'material-ui';
import {
  Table,
  TableBody,
  TableHeader,
  TableHeaderColumn,
  TableRow,
  TableRowColumn,
} from 'material-ui/Table';

var api_url = '/api/login/';
var api_url_scores = '/api/scores';

class Login extends Component {
  constructor(props) {
    super(props);

    // var scores = this.getScores();
    var scores =

    this.state = {
      username: '',
      password: '',
      username_error_text: '',
      password_error_text: '',
      zero: scores[0] ? scores[0] : 0,
      one: scores[1] ? scores[1] : 0,
      two: scores[2] ? scores[2] : 0
    }

    this.clear = this.clear.bind(this);
  }

  render() {
    return (
      <div align="center">
        <div>
          <AppBar title="Login" />
          <TextField
            hintText="Please enter your username"
            errorText={this.state.username_error_text}
            value={this.state.username}
            onChange={(event, newValue) => this.setState({ username: newValue })}
          />
          <br />
          <TextField
            type="password"
            hintText="password"
            floatingLabelText="Please enter your password"
            errorText={this.state.password_error_text}
            value={this.state.password}
            onChange={(event, newValue) => this.setState({ password: newValue })}
          />
          <br />
          <RaisedButton
            label="Login"
            secondary={true}
            // style={style}
            onClick={(event) => this.validateUserInput(event)}
          />
          <RaisedButton
            label="Clear"
            secondary={true}
            // style={style}
            onClick={this.clear}
          />
          <Table style={{ width: 200 }}>
            <TableHeader displaySelectAll={false}>
              <TableRow>
                <TableHeaderColumn>#</TableHeaderColumn>
                <TableHeaderColumn>Score</TableHeaderColumn>
              </TableRow>
            </TableHeader>
            <TableBody displayRowCheckbox={false} >
              <TableRow>
                <TableRowColumn>1</TableRowColumn>
                <TableRowColumn>{this.state.zero}</TableRowColumn>
              </TableRow>
              <TableRow>
                <TableRowColumn>2</TableRowColumn>
                <TableRowColumn>{this.state.one}</TableRowColumn>
              </TableRow>
              <TableRow>
                <TableRowColumn>3</TableRowColumn>
                <TableRowColumn>{this.state.two}</TableRowColumn>
              </TableRow>
            </TableBody>
          </Table>
        </div>
      </div>
    )
  }
}

