import React, { Component } from 'react';
import './App.css';
import Firebase from 'firebase';

class App extends Component {

  constructor() {
    super();
    this.state = {
      firstCard: 1,
      secondCard: 2,
      thirdCard: 3
    };
  }

  componentDidMount() {
    const rootRef = Firebase.database().ref().child('cards');

    rootRef.child('firstCard').on('value', snap => {
      this.setState({
        firstCard: snap.val()
      });
    });

    rootRef.child('secondCard').on('value', snap => {
      this.setState({
        secondCard: snap.val()
      });
    });

    rootRef.child('thirdCard').on('value', snap => {
      this.setState({
        thirdCard: snap.val()
      });
    });
  }

  render() {
    return (
      <div className="App">
        <h1>Your Cards</h1>
        <h2>First: {this.state.firstCard}</h2>
        <h2>Second: {this.state.secondCard}</h2>
        <h2>Third: {this.state.thirdCard}</h2>

      <div id="root">
        <div class="container">
          <input id="txtUsername" type="email" placeholder="Please enter your email"></input>
          <input id="txtPassword" type="password" placeholder="Please enter your password"></input>
          <button id="btnLogin" type="btn btn-action">Login</button>
          <button id="btnSignUp" type="btn btn-action">Sign up</button>
        </div>

      </div>
      </div>
    );
  }
}

export default App;
