import React, { Component } from 'react';
import { TextField, Button } from '@material-ui/core';
import Box from '@material-ui/core/Box';
import fire from '../config/Fire';

class Login extends Component {
  constructor(props) {
    super(props);

    this.login = this.login.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.signup = this.signup.bind(this);
    this.state = {
      email: '',
      password: ''
    };
  }

  handleChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  login(e) {
    e.preventDefault();
    fire.auth().signInWithEmailAndPassword(this.state.email, this.state.password).then((u) => {
    }).catch((error) => {
      console.log(error);
    });
  }

  signup(e) {
    e.preventDefault();
    fire.auth().createUserWithEmailAndPassword(this.state.email, this.state.password).then((u) => {
    }).then((u) => { console.log(u) })
      .catch((error) => {
        console.log(error);
      })
  }

  render() {
    return (
      <div align="center">
        <div className="Login">
          <form className={classes.root}>
            <Box m={2} pt={3}>
              <TextField id="email" label='Email' type="email" variant="outlined" value={this.state.email} onChange={this.handleChange} />
            </Box>
            <Box m={2} pt={3}>
              <TextField id="password" label='Password' type="password" variant="outlined" value={this.state.password} onChange={this.handleChange} />
            </Box>
            <Box m={2} pt={3}>
              <Button id="loginBtn" variant="contained" color="primary" onClick={this.login} >
                Login
              </Button>
            </Box>
            <Box m={2} pt={3}>
              <Button id="signupBtn" variant="contained" color="primary" onClick={this.signup}>
                Sign up
              </Button>
            </Box>
          </form>
        </div>
      </div>
    )
  }
}

export default Login;
