        return JSON.parse(localStorage.getItem('secondCard')) || ''
    }

    getThirdCard() {
        return JSON.parse(localStorage.getItem('thirdCard')) || ''
    }

    getFourthCard() {
        return JSON.parse(localStorage.getItem('fourthCard')) || ''
    }

