        console.log("getEnableSee(): " + JSON.parse(localStorage.getItem('enableSee')))
        if (JSON.parse(localStorage.getItem('enableSee') == null)) {
            return true
        } else {
            return JSON.parse(localStorage.getItem('enableSee'))
        }
