import React, { Component } from 'react';
import axios from 'axios';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import Card from './Card';
import ActionButtons from './ActionButtons'
import {deckArray} from '../utils/DeckArray'

var URL = "https://us-central1-teen-patti-5a5fc.cloudfunctions.net"

class Cards extends Component {
    constructor(props) {
        super(props);

        this.state = {
            cardsArray: deckArray,
            cards: [],
            message: '',
        }
    }

    shuffle = (array) => {
        let currentIndex = array.length, temporaryValue, randomIndex;
        while (0 !== currentIndex) {
          randomIndex = Math.floor(Math.random() * currentIndex);
          currentIndex -= 1;
          temporaryValue = array[currentIndex];
          array[currentIndex] = array[randomIndex];
          array[randomIndex] = temporaryValue;
        };
        this.setState({ cardsArray: array,})
        return array;
      };

    addItem(event) {
        event.preventDefault()
        const { cards } = this.state;
        const newItem = this.newItem.value;

        const isOnTheList = cards.includes(newItem)

        if (isOnTheList) {
            this.setState({
                message: 'This item is already on the list'
            })

        } else {
            return newItem !== '' && axios.post(URL + '/addCards', { item: newItem }).then((response) => {
                this.setState({
                    cards: response.data
                })
            })
        }

    }

    getCards(event) {
        event.preventDefault()

        return axios.get(URL + '/getCards').then((response) => {
            this.setState({
                cards: response.data
            })
        })
    }

    clearAll() {
        this.setState({
            cards: [],
            message: 'No cards in the list, add some'
        })
    }

    renderCards() {
        const { cards, message } = this.state;
        return (
            cards.map(item => {
                return <h3>{item.item}</h3>
            })
        )
    }

    render() {
        const { cards, message } = this.state;
        return (
            <div className="container">
                <Box m={2} pt={3}>
                    <Button variant="contained" color="primary" onClick={this.getCards.bind(this)} >Get Cards</Button>
                </Box>
                <h1>Your cards are:</h1>
                {this.renderCards()}
            </div>
        );
    }
}

export default Cards
