import React, { Component } from 'react';
import './App.css';
import Firebase from 'firebase';
import Login from './components/Login';

class App extends Component {

  constructor() {
    super();
    this.state = {
      firstCard: 1,
      secondCard: 2,
      thirdCard: 3
    };
  }

  componentDidMount() {
    const rootRef = Firebase.database().ref().child('cards');

    rootRef.child('firstCard').on('value', snap => {
      this.setState({
        firstCard: snap.val()
      });
    });

    rootRef.child('secondCard').on('value', snap => {
      this.setState({
        secondCard: snap.val()
      });
    });

    rootRef.child('thirdCard').on('value', snap => {
      this.setState({
        thirdCard: snap.val()
      });
    });
  }

  render() {
    return (
      <div className="App">
        <h2>Welcome to Teen Patti</h2>
        <Login/>
        <h1>Your Cards</h1>
        <h2>First: {this.state.firstCard}</h2>
        <h2>Second: {this.state.secondCard}</h2>
        <h2>Third: {this.state.thirdCard}</h2>
      </div>
    );
  }
}

export default App;
