import React, { Component } from 'react';
import axios from 'axios';
import { TextField, Button } from '@material-ui/core';
import Box from '@material-ui/core/Box';
import fire from 'firebase'

var URL = "https://us-central1-teen-patti-5a5fc.cloudfunctions.net"
const divStyle = {
    alignItems: 'center'
  };

class Cards extends Component {
    constructor(props) {
        super(props);

        this.getUserData = this.getUserData.bind(this);
        this.logout = this.logout.bind(this);
        this.state = {
            cards: [],
            message: '',
            userState: this.props.userState
        }
    }

    getUserData() {
        var userId = fire.auth().currentUser.uid;
        console.log(userId)
        return fire.database().ref('users/' + userId).once('value').then(function (snapshot) {
            var username = (snapshot.val() && snapshot.val().username) || 'Anonymous';
            console.log(username)
        });
    }

    logout() {
        fire.auth().signOut().then((result) => {
            this.setState({
                userState: null
            })
        })
    }

    componentDidMount() {
        fire.auth().onAuthStateChanged((user) => {
            if (user) {
                this.setState({
                    userState: user
                })
            }
        })

        // GET CARDS BRO
        // const dbRef = fire.database().ref().child('cards')
        // dbRef.on('value', snap => {
        //   snap.val().forEach((card) => {
        //     cards.push({
        //       suit: card.suit,
        //       card: card.card
        //     });
        //   })
        //   cards.forEach((card) => {
        //     console.log("Suit: " + card.suit + " Card: " + card.card)
        //   })
        // })
        // CREATE #_of_players hands
        let randomNumbers = new Set();
        for (let i = 0; i < 52; i++) {
            var rand = Math.floor((Math.random() * (51)));
            randomNumbers.add(rand);
        }

        var player1cards = [];
        var player2cards = [];
        var player3cards = [];
        var cardsSet = randomNumbers.values()
        player1cards.push(cardsSet.next().value)
        player1cards.push(cardsSet.next().value)
        player1cards.push(cardsSet.next().value)
        player2cards.push(cardsSet.next().value)
        player2cards.push(cardsSet.next().value)
        player2cards.push(cardsSet.next().value)
        player3cards.push(cardsSet.next().value)
        player3cards.push(cardsSet.next().value)
        player3cards.push(cardsSet.next().value)
        console.log(player1cards);
        console.log(player2cards);
        console.log(player3cards);
    }

    addItem(event) {
        event.preventDefault()
        const { cards } = this.state;
        const newItem = this.newItem.value;

        const isOnTheList = cards.includes(newItem)

        if (isOnTheList) {
            this.setState({
                message: 'This item is already on the list'
            })

        } else {
            return newItem !== '' && axios.post(URL + '/addCards', { item: newItem }).then((response) => {
                this.setState({
                    cards: response.data
                })
            })
        }
    }

    getCards(event) {
        event.preventDefault()
        return axios.get(URL + '/getCards').then((response) => {
            this.setState({
                cards: response.data
            })
        })
    }


    clearAll() {
        this.setState({
            cards: [],
            message: 'No cards in the list, add some'
        })
    }

    renderCards() {
        const { cards, message } = this.state;
        return (
            cards.map(item => {
                return <h3>{item.item}</h3>
            })
        )
    }

    render() {
        const { cards, message } = this.state;
        return (
            <div className="container" style={divStyle}>
                <Box m={2} pt={3}>
                    <Button variant="contained" color="primary" onClick={this.logout.bind(this)} >Logout</Button>
                </Box>
                <Box>
                    <Button variant="contained" color="primary" onClick={this.getUserData.bind(this)} >Get Cards</Button>
                </Box>
                <Box>
                    <TextField id="numPlayers" label="Number of players" value={this.state.password} onChange={this.handlePasswordFieldChange} helperText="Please enter the number of players" />
                </Box>
                <Box m={2} pt={3}>
                    <Button variant="contained" color="primary">SETUP GAME</Button>
                </Box>
                <h1>Your cards are:</h1>
                {this.renderCards()}
            </div>
        );
    }
}

export default Cards
