import React, { Component } from 'react';
import { AppBar, TextField, Button } from '@material-ui/core';
import Box from '@material-ui/core/Box';

var api_url = '/api/login/';
var api_url_scores = '/api/scores';

class Login extends Component {
  constructor(props) {
    super(props);

    // var scores = this.getScores();
    var scores = [1, 17, 23];

    this.state = {
      username: '',
      password: '',
      username_error_text: '',
      password_error_text: '',
      zero: scores[0] ? scores[0] : 0,
      one: scores[1] ? scores[1] : 0,
      two: scores[2] ? scores[2] : 0
    }

    this.clear = this.clear.bind(this);
  }

  render() {
    return (
      <div align="center">
        <div>
          <AppBar title="Login" />
          <Box m={2} pt={3}>
            <TextField id="username" label="Username" variant="outlined" value={this.state.username}
              onChange={(event, newValue) => this.setState({ username: newValue })} />
          </Box>
          <Box m={2} pt={3}>
            <TextField id="password" label="Password" variant="outlined" value={this.state.password} 
              onChange={(event, newValue) => this.setState({ usernapame: newValue })}/>
          </Box>
          <Box m={2} pt={3}>
            <Button variant="contained" color="primary">
              Login
            </Button>
          </Box>
