import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Firebase from 'firebase';

class App extends Component {

  constructor() {
    super();
    this.state = {
      firstCard: 1,
      secondCard: 2,
      thirdCard: 3
    };
  }

  componentDidMount() {
    const rootRef = Firebase.database().ref().child('cards');
    const cardRef = rootRef.child('card')
    cardRef.on('value', snap => {
      this.setState({
        firstCard: snap.val(),
        secondCard: snap.val(),
        thirdCard: snap.val()
      });
    });
  }

  render() {
    return (
      <div className="App">
        <h1>Cards</h1>
        <h2>First: {this.state.firstCard}</h2>
        <h2>Second: {this.state.speed}</h2>
        <h2>Third: {this.state.speed}</h2>
      </div>
    );
  }
}

export default App;
