import React, { Component } from 'react';
import { TextField, Button } from '@material-ui/core';
import Box from '@material-ui/core/Box';
import fire from '../config/Fire';

var api_url = '/api/login/';
var api_url_scores = '/api/scores';

class Login extends Component {
  constructor(props) {
    super(props);

    this.login = this.login.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.signup = this.signup.bind(this);
    this.state = {
      email: '',
      password: ''
    };
  }

  handleChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  componentDidMount() {
    this.authListener();
  }

  authListener() {
      fire.auth().onAuthStateChanged((user) => {
        console.log(user);
        if (user) {
          this.setState({ user })
        } else {
          this.setState({ user: null });
        }
      });
    }

  render() {
    return (
      <div align="center">
        <div className="Login">
          <Box m={2} pt={3}>
            <TextField id="username" label="Username" variant="outlined" value={this.state.username}
              onChange={(event, newValue) => this.setState({ username: newValue })} />
          </Box>
          <Box m={2} pt={3}>
            <TextField id="password" type="password" label="Password" variant="outlined" value={this.state.password} 
              onChange={(event, newValue) => this.setState({ password: newValue })}/>
          </Box>
          <Box m={2} pt={3}>
            <Button variant="contained" color="primary">
              Login
            </Button>
          </Box>
          <Box m={2} pt={3}>
            <Button variant="contained" color="primary">
              Sign up
            </Button>
          </Box>
        </div>
      </div>
    )
  }

  clear = () => {
    this.setState({
      username: '',
      password: ''
    });
  }
}

