import React, { Component } from 'react';
import axios from 'axios';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import fire from 'firebase'

var URL = "https://us-central1-teen-patti-5a5fc.cloudfunctions.net"

class Cards extends Component {
    constructor(props) {
        super(props);

        this.getUserData = this.getUserData.bind(this);
        this.logout = this.logout.bind(this);
        this.state = {
            cards: [],
            message: '',
            userState: this.props.userState
        }
    }

    getUserData() {
        var userId = fire.auth().currentUser.uid;
        console.log(userId)
        return fire.database().ref('users/' + userId).once('value').then(function (snapshot) {
            var username = (snapshot.val() && snapshot.val().username) || 'Anonymous';
            console.log(username)
        });
    }

    logout() {
        fire.auth().signOut().then((result) => {
            this.setState({
                userState: null
            })
        })
    }

    componentDidMount() {
        fire.auth().onAuthStateChanged((user) => {
            if (user) {
                this.setState({
                    userState: user
                })
            }
        })

        // GET CARDS BRO
        // const dbRef = fire.database().ref().child('cards')
        // dbRef.on('value', snap => {
        //   snap.val().forEach((card) => {
        //     cards.push({
        //       suit: card.suit,
        //       card: card.card
        //     });
        //   })
        //   cards.forEach((card) => {
        //     console.log("Suit: " + card.suit + " Card: " + card.card)
        //   })
        // })
        // CREATE #_of_players hands
        let randomNumbers = new Set();
        for (let i = 0; i < 52; i++) {
            var rand =  0 + Math.floor((Math.random() * (51)));
            randomNumbers.addItem(rand)
        }
        console.log(rand);
    }

    addItem(event) {
        event.preventDefault()
        const { cards } = this.state;
        const newItem = this.newItem.value;

        const isOnTheList = cards.includes(newItem)

        if (isOnTheList) {
            this.setState({
                message: 'This item is already on the list'
            })

        } else {
            return newItem !== '' && axios.post(URL + '/addCards', { item: newItem }).then((response) => {
                this.setState({
                    cards: response.data
                })
            })
        }
    }

    getCards(event) {
        event.preventDefault()
        return axios.get(URL + '/getCards').then((response) => {
            this.setState({
                cards: response.data
            })
        })
    }


    clearAll() {
        this.setState({
            cards: [],
            message: 'No cards in the list, add some'
        })
    }

    renderCards() {
        const { cards, message } = this.state;
        return (
            cards.map(item => {
                return <h3>{item.item}</h3>
            })
        )
    }

    render() {
        const { cards, message } = this.state;
        return (
            <div className="container">
                <Box m={2} pt={3}>
                    <Button variant="contained" color="primary" onClick={this.logout.bind(this)} >Logout</Button>
                </Box>
                <Box m={2} pt={3}>
                    <Button variant="contained" color="primary" onClick={this.getUserData.bind(this)} >Get Cards</Button>
                </Box>
                <h1>Your cards are:</h1>
                {this.renderCards()}
            </div>
        );
    }
}

export default Cards
