import React, { Component } from 'react';
import axios from 'axios';
import { TextField, Button } from '@material-ui/core';
import Box from '@material-ui/core/Box';
import fire from 'firebase'

var URL = "https://us-central1-teen-patti-5a5fc.cloudfunctions.net"
const divStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
};

class Cards extends Component {
    constructor(props) {
        super(props);

        this.getUserData = this.getUserData.bind(this);
        this.logout = this.logout.bind(this);
        this.handleNumPlayersFieldChange = this.handleNumPlayersFieldChange.bind(this);
        this.generate = this.generate.bind(this);
        this.resetGame = this.resetGame.bind(this);
        this.state = {
            cards: [],
            numPlayers: '',
            userState: this.props.userState,
            gameSet: true
        }
    }

    componentDidMount () {
        fire.database().ref('users/isGameSet').on('value', snap => {
            console.log('Before render component did mount:' + snap.val())
            this.setState({ gameSet: snap.val()})
        })
    }

    getUserData() {
        var userId = fire.auth().currentUser.uid;
        console.log(userId)
        return fire.database().ref('users/' + userId).once('value').then(function (snapshot) {
            var username = (snapshot.val() && snapshot.val().username) || 'Anonymous';
            console.log(username)
        });
    }

    logout() {
        fire.auth().signOut().then((result) => {
            this.setState({
                userState: null
            })
        })
    }

    resetGame() {
        // update isGameSet to database after reset game
        var updates = {};
        updates['users/' + 'isGameSet'] = false;
        fire.database().ref().update(updates);

        this.setState({ gameSet: false })
    }

    componentDidMount() {
        fire.auth().onAuthStateChanged((user) => {
            if (user) {
                this.setState({
                    userState: user
                })
            }
        })

        const dbRef = fire.database().ref('users/isGameSet')
        dbRef.on('value', snap => {
            console.log(snap.val())
            this.setState({
                gameSet: snap.val()
            })
        })

        const user = fire.database().ref().child('user').orderByKey();

        user.once('value', snap => {
            snap.forEach(child => {

            })
        })
        console.log("ALL USERS: " + fire.database().ref('users/').orderByValue()) 

        // GET CARDS BRO
        // const dbRef = fire.database().ref().child('cards')
        // dbRef.on('value', snap => {
        //   snap.val().forEach((card) => {
        //     cards.push({
        //       suit: card.suit,
        //       card: card.card
        //     });
        //   })
        //   cards.forEach((card) => {
        //     console.log("Suit: " + card.suit + " Card: " + card.card)
        //   })
        // })
    }

    generate(e) {
        e.preventDefault()

        let randomNumbers = new Set();
        let cardsTrips = [];
        for (let i = 0; i < 52; i++) {
            var rand = Math.floor((Math.random() * (51)));
            randomNumbers.add(rand);
        }

        var cardsSet = randomNumbers.values()

        for (let j = 0; j < this.state.numPlayers; j++) {
            cardsTrips[j] = [cardsSet.next().value, cardsSet.next().value, cardsSet.next().value]
        }

        console.log(cardsTrips);
        this.setState({ gameSet: true})

        // update isGameSet to database
        var updates = {};
        updates['users/' + 'isGameSet'] = true;
        fire.database().ref().update(updates);
    }

    addItem(event) {
        event.preventDefault()
        const { cards } = this.state;
        const newItem = this.newItem.value;

        const isOnTheList = cards.includes(newItem)

        if (isOnTheList) {
            this.setState({
                message: 'This item is already on the list'
            })

        } else {
            return newItem !== '' && axios.post(URL + '/addCards', { item: newItem }).then((response) => {
                this.setState({
                    cards: response.data
                })
            })
        }
    }

    getCards(event) {
        event.preventDefault()
        return axios.get(URL + '/getCards').then((response) => {
            this.setState({
                cards: response.data
            })
        })
    }


    clearAll() {
        this.setState({
            cards: [],
            message: 'No cards in the list, add some'
        })
    }

    renderCards() {
        const { cards, message } = this.state;
        return (
            cards.map(item => {
                return <h3>{item.item}</h3>
            })
        )
    }

    handleNumPlayersFieldChange(e) {
        e.preventDefault()
        this.setState({
            numPlayers: e.target.value
        })
    }

    render() {
        return (
            <div className="container">
                <div className="top_row" style={divStyle}>
                    { !this.state.gameSet && <div style={divStyle}>
                        <Box>
                            <TextField id="numPlayers" label="Number of players" value={this.state.numPlayers} onChange={this.handleNumPlayersFieldChange} helperText="Please enter the number of players" />
                        </Box>
                        <Box m={2} pt={3}>
                            <Button variant="contained" color="secondary" onClick={this.generate.bind(this)}>SETUP GAME</Button>
                        </Box> </div>}
                </div><br /><br />
                <Box m={2}>
                    <Button variant="contained" color="primary" onClick={this.logout.bind(this)} >Logout</Button>
                </Box>
                <Box m={2}>
                    <Button variant="contained" color="primary" onClick={this.resetGame.bind(this)} >Reset Game</Button>
                </Box>
                <Box>
                    <Button variant="contained" color="primary" onClick={this.getUserData.bind(this)} >Get Cards</Button>
                </Box>
                <h2>Your cards are:</h2>
                {this.renderCards()}
            </div>
        );
    }
}

export default Cards
